sparql:
# Sample Yale Alumni
# People with African American Ancestry born before 1940 who went to Yale University, Yale Divinity School, or Yale Law School
SELECT DISTINCT ?item ?itemLabel WHERE {
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
  {
    SELECT DISTINCT ?item WHERE {
      ?item p:P172 ?statement0.
      ?statement0 (ps:P172/(wdt:P279*)) wd:Q49085.
      {
        ?item p:P69 ?statement1.
        ?statement1 (ps:P69/(wdt:P279*)) wd:Q49112.
      }
      UNION
      {
        ?item p:P69 ?statement2.
        ?statement2 (ps:P69/(wdt:P279*)) wd:Q1143289.
      }
      UNION
      {
        ?item p:P69 ?statement2.
        ?statement2 (ps:P69/(wdt:P279*)) wd:Q10723329.
      }
      UNION
      {
        ?item p:P69 ?statement2.
        ?statement2 (ps:P69/(wdt:P279*)) wd:Q8047384.
      }
      ?item p:P569 ?statement_3.
      ?statement_3 psv:P569 ?statementValue_3.
      ?statementValue_3 wikibase:timePrecision ?precision_3.
      FILTER(?precision_3 >= 11 )
      ?statementValue_3 wikibase:timeValue ?P569_3.
      FILTER(?P569_3 < "+1940-01-01T00: 00: 00Z"^^xsd:dateTime)
    }
    LIMIT 100
  }
}
