SELECT DISTINCT ?item ?itemLabel WHERE {
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE]". }
  {
    SELECT DISTINCT ?item WHERE {
      ?item p:P106 ?statement0.
      ?statement0 (ps:P106/(wdt:P279*)) wd:Q1622272.
      {
        ?item p:P108 ?statement1.
        ?statement1 (ps:P108/(wdt:P279*)) wd:Q49112.
      }
      UNION
      {
        ?item p:P108 ?statement2.
        ?statement2 (ps:P108/(wdt:P279*)) wd:Q1143289.
      }
      UNION
      {
        ?item p:P108 ?statement3.
        ?statement3 (ps:P108/(wdt:P279*)) wd:Q10723329.
      }
    }
    LIMIT 30
  }
}
